window.addEventListener('load', start);

var unidadeArray = [
  '',
  'um',
  'dois',
  'três',
  'quatro',
  'cinco',
  'seis',
  'sete',
  'oito',
  'nove'
];

var dezenaArray = [
  '',
  'dez',
  'vinte',
  'trinta',
  'quarenta',
  'cinquenta',
  'sessenta',
  'setenta',
  'oitenta',
  'noventa',
];

var centenaArray = [
  '',
  'cento',
  'duzentos',
  'trezentos',
  'quatrocentos',
  'quinhentos',
  'seiscentos',
  'setecentos',
  'oitocentos',
  'novecentos',
];

var excecaoArray = []
excecaoArray[0] = 'zero';
excecaoArray[10] = 'dez';
excecaoArray[11] = 'onze';
excecaoArray[12] = 'doze';
excecaoArray[13] = 'treze';
excecaoArray[14] = 'quatorze';
excecaoArray[15] = 'quinze';
excecaoArray[16] = 'dezesseis';
excecaoArray[17] = 'dezessete';
excecaoArray[18] = 'dezoito';
excecaoArray[19] = 'dezenove';
excecaoArray[100] = 'cem';



function start() {
  preventSubmit();
}

function preventSubmit() {
  function handleSubmit(event) {
    event.preventDefault();
  }
}

var range = document.querySelector('#range');

range.addEventListener('input', function () {
  numAtual = document.querySelector('#numAtual');
  numAtual.value = this.value;
  numExtenso = document.querySelector('#numExtenso');
  numExtenso.value = convertionNumbersToText(this.value);
});

function convertionNumbersToText(numeroEmString) {
  // faltou lista de exceção não funciona nas centenas
  var unidade = 0;
  var dezena = 0;
  var centena = 0;

  if (excecaoArray[numeroEmString] != undefined) {
    return excecaoArray[numeroEmString];
  }
  if (numeroEmString.length == 1) {
    unidade = numeroEmString[0];
  }
  if (numeroEmString.length == 2) {
    dezena = numeroEmString[0];
    unidade = numeroEmString[1];
  }
  if (numeroEmString.length == 3) {
    centena = numeroEmString[0];
    dezena = numeroEmString[1];
    unidade = numeroEmString[2];

  }
  returnText = '';
  if (centenaArray[centena] != '') {
    returnText += centenaArray[centena];
  }
  if (dezenaArray[dezena] != '') {
    if (returnText.length > 0) {
      returnText += ' e ' + dezenaArray[dezena];
    } else {
      returnText += dezenaArray[dezena];
    }
  }
  if (returnText.length > 0 && unidadeArray[unidade] != '') {
    returnText += ' e ' + unidadeArray[unidade];
  } else {
    returnText += unidadeArray[unidade];
  }

  return returnText;
}